package javabasics;


public class Printevenodd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i, j;
		for(i=0; i<=10; i++)
		{
			if(i%2==0)
			System.out.println("Even number" +i);
		}
		for(j=0; j<=10; j++)
		{
			if(j%2==1)
			System.out.println("Odd number" +j);
		}	
	
	}

}
